\documentclass[11pt,a4paper]{article}
\usepackage[utf8]{inputenc}
\usepackage[english]{babel}
\usepackage{amssymb}
\usepackage{bussproofs}

\begin{document}

\noindent {\bfseries\LARGE Rules of $\lambda$-cube \par}
\bigskip\noindent

(sort)
$$\varnothing \vdash *: \square$$

(var)
\begin{prooftree}
    \AxiomC{\(\Gamma \vdash A: s\)}
    \UnaryInfC{\(\Gamma, x: A \vdash x: A\)}
\end{prooftree}

(weak)
\begin{prooftree}
    \AxiomC{\(\Gamma \vdash A: B\)}
    \AxiomC{\(\Gamma \vdash C: s\)}
    \BinaryInfC{\(\Gamma, x: C \vdash A: B\)}
\end{prooftree}

(form)
\begin{prooftree}
    \AxiomC{\(\Gamma \vdash A: s_1\)}
    \AxiomC{\(\Gamma, x: A \vdash B: s_2\)}
    \BinaryInfC{\(\Gamma \vdash \Pi x: A. B: s_2\)}
\end{prooftree}

(appl)
\begin{prooftree}
    \AxiomC{\(\Gamma \vdash M: \Pi x: A. B\)}
    \AxiomC{\(\Gamma \vdash N: A\)}
    \BinaryInfC{\(\Gamma \vdash MN: B[x := N]\)}
\end{prooftree}

(abst)
\begin{prooftree}
    \AxiomC{\(\Gamma, x: A \vdash M: B\)}
    \AxiomC{\(\Gamma \vdash \Pi x: A. B: s\)}
    \BinaryInfC{\(\Gamma \vdash \lambda x: A. M: \Pi x: M. B\)}
\end{prooftree}

(conv)
\begin{prooftree}
    \AxiomC{\(\Gamma \vdash A: B\)}
    \AxiomC{\(\Gamma \vdash B': s\)}
    \BinaryInfC{\(\Gamma \vdash A: B'\)}
\end{prooftree}

\end{document}
